import { useState, useEffect } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { ArrowLeft, RefreshCw } from "lucide-react";
import { useLocation } from "wouter";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";


interface WingoResult {
  issueNumber: string;
  number: number;
  timestamp: number;
}

interface WingoPrediction {
  period: string;
  prediction: "BIG" | "SMALL";
  predictedNumber: number;
  confidence: number;
  countdown: number;
}

export default function Wingo30Sec() {
  const [, navigate] = useLocation();
  const [countdown, setCountdown] = useState(30);
  const { toast } = useToast();

  // Fetch prediction data
  const { data: prediction } = useQuery<WingoPrediction>({
    queryKey: [`/api/wingo/prediction/30sec`],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  // Fetch results data  
  const { data: results = [] } = useQuery<WingoResult[]>({
    queryKey: [`/api/wingo/results/30sec`],
    refetchInterval: 15000, // Refetch every 15 seconds
  });



  // Synchronized countdown timer with server
  useEffect(() => {
    // Always sync with server countdown when prediction updates
    if (prediction?.countdown !== undefined) {
      setCountdown(prediction.countdown);
    }
  }, [prediction]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCountdown(prev => {
        const newValue = prev - 1;
        // If countdown would go to 0 or below, keep it at 1 until server updates
        return newValue <= 0 ? 1 : newValue;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const getBigSmall = (number: number): "BIG" | "SMALL" => {
    return number >= 5 ? "BIG" : "SMALL";
  };

  const formatPeriodNumber = (period: string): string => {
    if (period && period.startsWith('2') && period.length >= 3) {
      return '20' + period.substring(1);
    }
    return period;
  };

  const getCurrentISTTime = (): string => {
    const now = new Date();
    return now.toLocaleTimeString('en-IN', { 
      timeZone: 'Asia/Kolkata',
      hour12: false,
      hour: '2-digit', 
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const userIds = [
    "2643211", "3077777", "2525003", "2955005", "2977770", "2834000", "2521120", "2966006", 
    "2756789", "2677722", "2701203", "2830901", "2530491", "3010000", "3060808", "3099999", 
    "3088888", "3243210", "2890001", "3045678", "2999999", "3113123", "3130000", "3288888", 
    "2741112", "3250000", "3211000", "2922222", "3111000", "3123123", "2849081", "2940101", 
    "2733333", "2667722", "2601000", "2800123", "2720000", "2863456", "2689100", "2911001", 
    "3276543", "3050005", "2903452", "2825678", "2741112", "3222222", "3234567", "2988881", 
    "2760001", "2879000", "2583021", "3301234", "3156789", "2900001", "2712205", "3200000", 
    "2825678", "3178888", "3045678", "2701203", "3288888", "3211000", "3010000", "3111000", 
    "3344444", "3088888", "3265432", "2999999", "2521120"
  ];

  const maskUserId = (userId: string): string => {
    if (userId.length >= 4) {
      const first2 = userId.substring(0, 2);
      const last2 = userId.substring(userId.length - 2);
      const middle = 'x'.repeat(userId.length - 4);
      return `${first2}${middle}${last2}`;
    }
    return userId;
  };

  const getUserParticipation = (): string[] => {
    // Show only 4 random users that have "joined" the current prediction
    const shuffled = [...userIds].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, 4);
  };





  return (
    <div className="min-h-screen p-4" style={{ backgroundColor: '#1a1318' }}>
      <div style={{ maxWidth: '300px', margin: '0 auto', backgroundColor: '#231C21', borderRadius: '8px', overflow: 'hidden' }}>
        {/* Header */}
        <div className="bg-banner-gradient border-b-2 border-accent-gold/30 px-4 py-4">
          <div className="flex items-center justify-between mb-3">
            <button
              onClick={() => navigate('/')}
              className="flex items-center gap-2 text-white hover:text-yellow-400 transition-colors"
            >
              <ArrowLeft className="w-5 h-5" />
              <span>Back</span>
            </button>
            <h1 className="text-xl font-bold light-gold">Wingo 30Sec</h1>
            <button
              onClick={() => {
                queryClient.invalidateQueries({ queryKey: ['/api/wingo/prediction/30sec'] });
                queryClient.invalidateQueries({ queryKey: ['/api/wingo/results/30sec'] });
              }}
              className="flex items-center justify-center w-10 h-10 text-white hover:text-yellow-400 transition-colors rounded-lg hover:bg-white/10"
              title="Refresh"
            >
              <RefreshCw className="w-5 h-5" />
            </button>
          </div>

          {/* Issue Number and Countdown */}
          <div className="text-center space-y-2">
            <div className="text-sm warm-gold">Issue Number</div>
            <div className="text-lg font-bold light-gold">{formatPeriodNumber(prediction?.period || "Loading...")}</div>
            <div className="text-sm warm-gold">Next Round In</div>
            <div className="text-2xl font-bold text-red-400">{countdown}s</div>
          </div>
        </div>

        {/* Main Content */}
        <div className="space-y-4 p-4">
        {/* Current Prediction Card */}
        <Card className="bg-gradient-to-br from-gray-800 to-gray-900 border-2" style={{ borderColor: '#FED358' }}>
          <div className="p-4 text-center space-y-4">
            <div className="space-y-1">
              <h2 className="text-lg sm:text-xl font-bold text-white">
                üèÜ V3 GAME WINGO 30SEC üèÜ
              </h2>
              <div className="h-0.5 bg-gradient-to-r from-transparent via-yellow-400 to-transparent"></div>
            </div>

            <div className="space-y-3">
              <div className="text-gray-300 text-sm sm:text-base">
                üîì <span className="font-bold">PERIOD ID</span> - {formatPeriodNumber(prediction?.period || "Loading...")}
              </div>

              <div className="flex gap-2 sm:gap-4 justify-center items-center">
                <div 
                  className="text-2xl sm:text-3xl font-black tracking-wider py-2 px-3 sm:py-3 sm:px-4 rounded-lg border-2 bg-black/20 shadow-lg"
                  style={{ 
                    color: '#FED358',
                    borderColor: '#FED358',
                    textShadow: '0 0 15px rgba(254, 211, 88, 0.5)'
                  }}
                >
                  {prediction?.prediction || "LOADING"}
                </div>
                <div className="text-gray-400 text-xl font-bold">+</div>
                <div 
                  className="text-3xl sm:text-4xl font-black tracking-wider py-2 px-3 sm:py-3 sm:px-4 rounded-lg border-2 bg-black/20 shadow-lg w-16 h-16 sm:w-20 sm:h-20 flex items-center justify-center"
                  style={{ 
                    color: '#FED358',
                    borderColor: '#FED358',
                    textShadow: '0 0 15px rgba(254, 211, 88, 0.5)'
                  }}
                >
                  {prediction?.predictedNumber ?? "?"}
                </div>
              </div>

              <div className="text-gray-300 text-sm sm:text-base">
                ‚è∞ Next prediction in: <span className="font-bold text-white text-lg">{countdown}s</span>
              </div>

              {prediction?.confidence && (
                <div className="text-sm text-gray-400">
                  Confidence: <span style={{ color: '#FED358' }}>{prediction.confidence}%</span>
                </div>
              )}
            </div>

            <div className="pt-3 space-y-2">
              <Button 
                className="w-full text-black font-bold text-base py-3 hover:opacity-90 transition-all duration-200 shadow-lg"
                style={{ backgroundColor: '#FED358' }}
                onClick={() => window.open('https://www.v3game6.com/#/', '_blank')}
              >
                üéÆ Play Now - V3 GAME üéÆ
              </Button>

              <div className="text-xs text-gray-400 italic">
                Game Link: <span className="text-blue-400">https://www.v3game6.com/#/</span>
              </div>
            </div>
          </div>
        </Card>



        {/* VIP Prediction */}
        <Card className="bg-gradient-to-br from-gray-800 to-gray-900 border border-gray-700">
          <div className="p-3 border-b border-gray-700">
            <h3 className="text-base font-bold text-white flex items-center gap-2 ml-2">
              üéØ VIP Prediction
            </h3>
          </div>
          <div className="p-3">
            <div className="grid grid-cols-3 gap-3 text-center ml-2">
              <div>
                <div className="text-xs text-gray-400 mb-1">Color</div>
                <div 
                  className="w-6 h-6 rounded mx-auto"
                  style={{ 
                    backgroundColor: prediction?.prediction === 'BIG' ? '#ff4444' : '#00ff88'
                  }}
                ></div>
              </div>
              <div>
                <div className="text-xs text-gray-400 mb-1">Size</div>
                <div className="text-white font-bold text-base">
                  {prediction?.prediction || "Loading..."}
                </div>
              </div>
              <div>
                <div className="text-xs text-gray-400 mb-1">Number</div>
                <div className="text-white font-bold text-base">
                  {prediction?.predictedNumber ?? "?"}
                </div>
              </div>
            </div>
          </div>
        </Card>

        {/* Live Players Section */}
        <Card className="bg-gradient-to-br from-gray-800 to-gray-900 border border-gray-700">
          <div className="p-3 border-b border-gray-700">
            <h3 className="text-base font-bold text-white flex items-center gap-2 ml-2">
              üë• Live Players
            </h3>
          </div>
          <div className="p-3">
            <div className="space-y-1 text-center ml-2">
              {getUserParticipation().map((userId, index) => (
                <div key={index} className="text-gray-300 text-xs">
                  <span className="text-yellow-400">{maskUserId(userId)}</span> has joined{' '}
                  <span className={`font-bold ${prediction?.prediction === 'BIG' ? 'text-green-400' : 'text-red-400'}`}>
                    {prediction?.prediction || 'BIG'}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </Card>
        </div>
      </div>
    </div>
  );
}

