          {/* User Participation Section */}
          <div className="mt-4">
            <div className="flex items-center gap-3 mb-3">
              <div className="w-1 h-6 bg-blue-400 rounded-full"></div>
              <h3 className="text-white font-bold text-lg">Live Players</h3>
